# VVVVCJJCFE
# VVIVCCJJEE
# VVIIICJJEE
# MIIIIIJJEE
# MIIISIJEEE
# MMMISSJEEE"""
dr = [0,1,0,-1]
dc = [1,0,-1,0]
garden = input.split("\n")
r_len = len(garden)
c_len = len(garden[0])

is_visited = [[0] * c_len for _ in range(r_len)]

def calculate_fence_cost(r,c):
    plant = garden[r][c]
    area = 0
    length = 0
    que = deque()
    que.append((r,c))
    is_visited[r][c] = 1
    while que:
        cur_r, cur_c = que.popleft()
        area += 1
        for j in range(4):
            r = cur_r + dr[j]
            c = cur_c + dc[j]
            if r < 0 or r >= r_len or c < 0 or c >= c_len or garden[r][c] != plant:
                length += 1
        for i in range(4):
            nr = cur_r + dr[i]
            nc = cur_c + dc[i]
            if nr < 0 or nr >= r_len or nc < 0 or nc >= c_len:
                continue
            if garden[nr][nc] == plant and is_visited[nr][nc] == 0:
                que.append((nr, nc))
                is_visited[nr][nc] = 1
    print(plant, end=" ")
    print(area, end=" ")
    print(length)
    return area * length
answer = 0
for i in range(r_len):
    for j in range(c_len):
        if is_visited[i][j] == 0:
            answer += calculate_fence_cost(i,j)

print(answer)
